package chap04.Ifelse;

public class dan29 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int dan = 2; dan<10; dan++,System.out.println()) {
			
			for(int i = 1; i<10; i++) {
				
				System.out.printf("%d * %d = %2d\t",dan,i,dan*i);
				
			}
			
		}
		
		
	}

}
